mapper.update:
UPDATE BOARD_HD
SET TITLE = #{title}
    , CONTENT = #{content}
    , UPDATE_ID = #{userId}
    , UPDATE_DT = SYSDATE
    , HEADER_SE_CD = #{headerSeCd}
WHERE BOARD_ID = #{boardId}

UPDATE BOARD_HD
SET VIEW_COUNT = (
    SELECT VIEW_COUNT + 1
    FROM BOARD_HD
    WHERE BOARD_ID = #{boardId}
)
WHERE BOARD_ID = #{boardId}

UPDATE BOARD_HD
SET SUGGESTION_COUNT = (
    SELECT SUGGESTION_COUNT + 1
    FROM BOARD_HD
    WHERE BOARD_ID = #{boardId}
)
WHERE BOARD_ID = #{boardId}
